cortex build
pid=`lsof -i :1976 | awk 'NR > 1 {print $2}'`
if [[ ! -z $pid ]];then
	kill $pid
fi
export ENV=beta
cortex bundle -o ./demo/standalone.js --path http://i{n}.dpfile.com/mod/
cortex test --neuron-path='http://s2.51ping.com/mod' --prerelease=beta &
sleep 2
pid=`lsof -i :1976 | awk 'NR > 1 {print $2}'`
echo $pid
curl http://127.0.0.1:1976/dpapp-core.html > demo/test.html
gulp ftp
kill $pid
exit 0