if [[ $ENV != 'product' ]];then
  PRERELEASE="--prerelease $ENV"
else
  PRERELEASE=""
fi

cortex bundle --help > /dev/null
if [ "$?" = "1" ]; then
	echo "npm install cortex-bundle -g"
	npm install cortex-bundle -g
fi

if [ -z "$ENV" ]; then
  echo "cortex bundle -o ./src/standalone.js --path http://j1.s{n}.dpfile.com/mod/"
  cortex bundle -o ./src/standalone.js --path http://j1.s{n}.dpfile.com/mod/
  exit 0
fi

echo "cortex bundle --built-root $CORTEX_DEST $PRERELEASE -o $CORTEX_DEST/$CORTEX_NAME/$CORTEX_VERSION/src/standalone.js --path http://www.dpfile.com/mod/"
cortex bundle --built-root $CORTEX_DEST $PRERELEASE -o $CORTEX_DEST/$CORTEX_NAME/$CORTEX_VERSION/src/standalone.js --path http://www.dpfile.com/mod/

