const assert = require('assert');
const KNB = require('../compiled/wx/index');
KNB.ready = (cb) => cb();

describe('wx isApiSupported method test', function () {
    it('should return true', function (done) {
        KNB.isApiSupported({
            apiName: 'getUA',
            success: function (result) {
                assert.ok(result);
                done();
            }
        })
    });

    it('should return false', function (done) {
        KNB.isApiSupported({
            apiName: 'notImplement',
            success: function (result) {
                assert.equal(result, false);
                done();
            }
        });
    });

    it('should return false', function (done) {
        KNB.isApiSupported({
            apiName: 'setPullDown',
            success: function (result) {
                assert.equal(result, false);
                done();
            }
        });
    });
});
