var path = require('path');
var fs = require('fs');
var home = require('home');

var base = process.env.DP_FTP_CONFIG_BASE || "~/.gulp/";
var env = process.env.ENV;

var getConfig = module.exports = function(prefix, name){
	if(!env){
		throw "ERR: $ENV is not defined";
	}
	function isTrue(a){
		return !!a;
	}
	console.log(path.join(home.resolve(base), [prefix, env, name].filter(isTrue).join('_') + ".json"));
	return require( path.join(home.resolve(base), [prefix, env, name].filter(isTrue).join('_') + ".json"));
}